import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';

@Component({
  selector: 'app-zodiaco',
  standalone: true,
  imports: [ CommonModule, ReactiveFormsModule],
  templateUrl: './zodiaco.component.html',
  styleUrl: './zodiaco.component.css'
})

export class ZodiacoComponent implements OnInit {
  signo!: FormGroup;  
  resultado: boolean = false;
  nombreCompleto: string = '';
  edad: number = 0;
  signoChino: string = '';
  signoImagen: string = '';

  signos = [
    { nombre: 'Rata', anio: [1924, 1936, 1948, 1960, 1972, 1984, 1996, 2008, 2020], imagen: 'data:image/png;base64,' },
    { nombre: 'Buey', anio: [1925, 1937, 1949, 1961, 1973, 1985, 1997, 2009, 2021], imagen: 'data:image/png;base64,' },
    { nombre: 'Tigre', anio: [1926, 1938, 1950, 1962, 1974, 1986, 1998, 2010, 2022], imagen: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS5_-2vtajPrJQ8Jlx4Z4-uu7TMm3Ehrk-DFQ&s' },
    { nombre: 'Conejo', anio: [1927, 1939, 1951, 1963, 1975, 1987, 1999, 2011, 2023], imagen: 'data:image/png;base64,' },
    { nombre: 'Drag√≥n', anio: [1928, 1940, 1952, 1964, 1976, 1988, 2000, 2012, 2024], imagen: 'data:image/png;base64,' },
    { nombre: 'Serpiente', anio: [1929, 1941, 1953, 1965, 1977, 1989, 2001, 2013, 2025], imagen: 'data:image/png;base64,' },
    { nombre: 'Caballo', anio: [1930, 1942, 1954, 1966, 1978, 1990, 2002, 2014, 2026], imagen: 'data:image/png;base64,' },
    { nombre: 'Cabra', anio: [1931, 1943, 1955, 1967, 1979, 1991, 2003, 2015, 2027], imagen: 'data:image/png;base64,' },
    { nombre: 'Mono', anio: [1932, 1944, 1956, 1968, 1980, 1992, 2004, 2016, 2028], imagen: 'data:image/png;base64,' },
    { nombre: 'Gallo', anio: [1933, 1945, 1957, 1969, 1981, 1993, 2005, 2017, 2029], imagen: 'data:image/png;base64,' },
    { nombre: 'Perro', anio: [1934, 1946, 1958, 1970, 1982, 1994, 2006, 2018, 2030], imagen: 'data:image/png;base64,' },
    { nombre: 'Cerdo', anio: [1935, 1947, 1959, 1971, 1983, 1995, 2007, 2019, 2031], imagen: 'data:image/png;base64,' }
  ];

  ngOnInit(): void {
    this.signo = new FormGroup({
      nombre: new FormControl('', Validators.required),
      apellidoPaterno: new FormControl('', Validators.required),
      apellidoMaterno: new FormControl('', Validators.required),
      sexo: new FormControl('', Validators.required),
      dia: new FormControl('', [Validators.required, Validators.min(1), Validators.max(31)]),  
      mes: new FormControl('', [Validators.required, Validators.min(1), Validators.max(12)]),  
      anio: new FormControl('', [Validators.required, Validators.min(1900), Validators.max(new Date().getFullYear())])
    });
  }

  Signo() {
    const { nombre, apellidoPaterno, apellidoMaterno, anio, mes, dia } = this.signo.value;
    const currentDate = new Date();
    const birthDate = new Date(anio, mes - 1, dia);

    this.edad = currentDate.getFullYear() - anio;
    if (currentDate.getMonth() < mes - 1 || (currentDate.getMonth() === mes - 1 && currentDate.getDate() < dia)) {
      this.edad--;
    }

    this.nombreCompleto = `${nombre} ${apellidoPaterno} ${apellidoMaterno}`;
    const signo = this.signos.find(s => s.anio.includes(anio)) || { nombre: '', imagen: '' };
    this.signoChino = signo.nombre;
    this.signoImagen = signo.imagen;
    this.resultado = !!signo.nombre;
  }
}
